import React, { useState, useRef, useEffect } from 'react';
import '../../styles/AnatomySection.css';

const AnatomySection = () => {
  const [activeIndicator, setActiveIndicator] = useState(null);
  const iframeRef = useRef(null);

  const showIndicator = (indicator) => {
    setActiveIndicator(indicator);
  };

  const hideIndicator = () => {
    setActiveIndicator(null);
  };

  // Function to send messages to the iframe
  const sendMessageToIframe = (action, params = {}) => {
    if (iframeRef.current && iframeRef.current.contentWindow) {
      const message = { action, params };
      iframeRef.current.contentWindow.postMessage(message, '*');
    }
  };

  // Setup event listener for messages from the iframe
  useEffect(() => {
    const handleMessage = (event) => {
      // Handle any messages from the BioDigital Human iframe
      if (event.data && event.data.type) {
        switch(event.data.type) {
          case 'hover':
            // When hovering over a part in the 3D model
            const anatomyPart = event.data.part?.toLowerCase() || '';
            if (anatomyPart.includes('heart')) {
              showIndicator('heart');
            } else if (anatomyPart.includes('bone') || anatomyPart.includes('skeletal')) {
              showIndicator('bone');
            } else if (anatomyPart.includes('teeth') || anatomyPart.includes('dental')) {
              showIndicator('teeth');
            } else {
              hideIndicator();
            }
            break;
          default:
            // Other message types can be handled here
            break;
        }
      }
    };

    window.addEventListener('message', handleMessage);
    return () => window.removeEventListener('message', handleMessage);
  }, []);

  return (
    <div className="anatomy-section">
      <div className="human-container">
        <div className="health-indicators">
          {activeIndicator === 'heart' && (
            <div className="indicator heart-indicator">
              <div className="indicator-dot"></div>
              <div className="indicator-label">Healthy Heart</div>
            </div>
          )}
          {activeIndicator === 'bone' && (
            <div className="indicator bone-indicator">
              <div className="indicator-dot"></div>
              <div className="indicator-label">Healthy Bone</div>
            </div>
          )}
          {activeIndicator === 'teeth' && (
            <div className="indicator teeth-indicator">
              <div className="indicator-dot"></div>
              <div className="indicator-label">Healthy Teeth</div>
            </div>
          )}
        </div>

        <div className="body-model-container">
          <iframe 
            ref={iframeRef}
            className="human-3d-model"
            src="https://human.biodigital.com/widget/?be=2PcB&background.colors=0,0,0,1,0,0,0,1&initial.hand-hint=true&ui-fullscreen=true&ui-center=false&ui-dissect=true&ui-zoom=true&ui-help=true&ui-tools-display=primary&ui-info=true&uaid=3YfOR"
            allowFullScreen
            title="BioDigital Human"
          ></iframe>
        </div>

        <div className="anatomy-controls">
          <div className="control-button" onClick={() => sendMessageToIframe('rotate', { axis: 'y', angle: -90 })}>
            <span>Rotate Left</span>
          </div>
          <div className="control-button" onClick={() => sendMessageToIframe('rotate', { axis: 'y', angle: 90 })}>
            <span>Rotate Right</span>
          </div>
          <div className="control-button" onClick={() => sendMessageToIframe('reset')}>
            <span>Reset View</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AnatomySection;
