import React, { useState } from 'react';
import '../../styles/CalendarView.css';

const CalendarView = () => {
  const [selectedDay, setSelectedDay] = useState(28); // Default to the 28th

  // Calendar data for October 2021
  const calendarData = {
    month: 'October',
    year: '2021',
    days: [
      { day: 'Mon', date: 25 },
      { day: 'Tues', date: 26 },
      { day: 'Wed', date: 27 },
      { day: 'Thurs', date: 28 },
      { day: 'Fri', date: 29 },
      { day: 'Sat', date: 30 },
      { day: 'Sun', date: 31 }
    ]
  };

  // Time slots data exactly as per the requirements
  const timeSlotData = [
    // Time, day of week index (0=Mon), status (0=available, 1=booked, 2=unavailable)
    { time: '10:00', slots: [{day: 0, status: 0}, {day: 3, status: 0}, {day: 6, status: 0}] },
    { time: '11:00', slots: [{day: 0, status: 0}, {day: 3, status: 1}, {day: 6, status: 0}] },
    { time: '12:00', slots: [{day: 0, status: 0}, {day: 2, status: 0}, {day: 5, status: 1}] },
    { time: '08:00', slots: [{day: 1, status: 0}] },
    { time: '09:00', slots: [{day: 1, status: 1}, {day: 5, status: 1}] },
    { time: '10:00', slots: [{day: 1, status: 0}] },
    { time: '13:00', slots: [{day: 2, status: 0}] },
    { time: '14:00', slots: [{day: 4, status: 0}, {day: 5, status: 0}] },
    { time: '15:00', slots: [{day: 5, status: 0}] },
    { time: '16:00', slots: [{day: 4, status: 0}] }
  ];

  // Appointment details that will appear below the calendar
  const appointmentDetails = [
    { 
      type: 'Dentist', 
      time: '09:00-11:00', 
      doctor: 'Dr. Cameron Williamson',
      day: 'Thursday',
      icon: 'tooth' 
    },
    { 
      type: 'Physiotherapy Appointment', 
      time: '11:00-12:00', 
      doctor: 'Dr. Kevin Diones',
      day: 'Thursday',
      icon: 'physio' 
    },
    { 
      type: 'Health checkup complete', 
      time: '11:00 AM', 
      day: 'Thursday',
      icon: 'checkup' 
    },
    { 
      type: 'Ophthalmologist', 
      time: '14:00 PM', 
      day: 'Thursday',
      icon: 'eye' 
    },
    { 
      type: 'Cardiologist', 
      time: '12:00 AM', 
      day: 'Saturday',
      icon: 'heart' 
    },
    { 
      type: 'Neurologist', 
      time: '16:00 PM', 
      day: 'Saturday',
      icon: 'brain' 
    }
  ];

  // Helper function to get slot status for a specific day and time
  const getSlotStatus = (dayIndex, timeStr) => {
    const timeData = timeSlotData.find(item => {
      return item.time === timeStr && item.slots.some(slot => slot.day === dayIndex);
    });

    if (!timeData) return 2; // Unavailable/empty

    const slot = timeData.slots.find(slot => slot.day === dayIndex);
    return slot ? slot.status : 2; // Return status or unavailable if not found
  };

  // Get appointments for a specific day (Thursday or Saturday)
  const getDayAppointments = (day) => {
    return appointmentDetails.filter(apt => apt.day === day);
  };

  // Thursday appointments
  const thursdayAppointments = getDayAppointments('Thursday');

  // Saturday appointments
  const saturdayAppointments = getDayAppointments('Saturday');

  return (
    <div className="calendar-view">
      <div className="october-calendar">
        <div className="calendar-header">
          <div className="month-selector">
            <span className="current-month">{calendarData.month} {calendarData.year}</span>
            <div className="month-arrows">
              <button className="month-arrow prev"><span>‚Äπ</span></button>
              <button className="month-arrow next"><span>‚Ä∫</span></button>
            </div>
          </div>
        </div>

        <div className="calendar-grid">
          {/* Day headers */}
          <div className="days-header">
            {calendarData.days.map((day, index) => (
              <div 
                key={day.date} 
                className={`day-header ${day.date === selectedDay ? 'selected' : ''}`}
                onClick={() => setSelectedDay(day.date)}
              >
                <div className="day-name">{day.day}</div>
                <div className="day-number">{day.date}</div>
              </div>
            ))}
          </div>

          {/* Time slots grid */}
          <div className="time-slots-container">
            {/* Each row represents a time slot across all days */}
            {calendarData.days.map((day, dayIndex) => (
              <div key={`day-${day.date}`} className="day-column">
                {/* Display time slots with appropriate styling */}
                {['10:00', '11:00', '12:00', '‚Äî', '‚Äî', '‚Äî'].map((time, timeIndex) => {
                  if (time === '‚Äî') return <div key={`empty-${dayIndex}-${timeIndex}`} className="empty-slot">‚Äî</div>;

                  const status = getSlotStatus(dayIndex, time);
                  let displayTime = time;

                  // Special cases for specific days and times
                  if (dayIndex === 1 && timeIndex === 0) displayTime = '08:00';
                  if (dayIndex === 1 && timeIndex === 1) displayTime = '09:00';
                  if (dayIndex === 1 && timeIndex === 2) displayTime = '10:00';
                  if (dayIndex === 2 && timeIndex === 0) displayTime = '12:00';
                  if (dayIndex === 2 && timeIndex === 1) displayTime = '13:00';
                  if (dayIndex === 2 && timeIndex === 2) displayTime = '‚Äî';
                  if (dayIndex === 3 && timeIndex === 0) displayTime = '10:00';
                  if (dayIndex === 3 && timeIndex === 1) displayTime = '11:00';
                  if (dayIndex === 3 && timeIndex === 2) displayTime = '‚Äî';
                  if (dayIndex === 4 && timeIndex === 0) displayTime = '‚Äî';
                  if (dayIndex === 4 && timeIndex === 1) displayTime = '14:00';
                  if (dayIndex === 4 && timeIndex === 2) displayTime = '16:00';
                  if (dayIndex === 5 && timeIndex === 0) displayTime = '12:00';
                  if (dayIndex === 5 && timeIndex === 1) displayTime = '14:00';
                  if (dayIndex === 5 && timeIndex === 2) displayTime = '15:00';
                  if (dayIndex === 6 && timeIndex === 0) displayTime = '10:00';
                  if (dayIndex === 6 && timeIndex === 1) displayTime = '10:00';
                  if (dayIndex === 6 && timeIndex === 2) displayTime = '11:00';

                  // Special case for booked slots
                  const isBooked = (dayIndex === 1 && timeIndex === 1) || 
                                 (dayIndex === 3 && timeIndex === 1) || 
                                 (dayIndex === 5 && timeIndex === 0);

                  return (
                    <div 
                      key={`slot-${dayIndex}-${timeIndex}`} 
                      className={`time-slot ${isBooked ? 'booked' : ''} ${displayTime === '‚Äî' ? 'empty' : ''}`}
                    >
                      {displayTime !== '‚Äî' ? displayTime : '‚Äî'}
                    </div>
                  );
                })}
              </div>
            ))}
          </div>
        </div>

        {/* Appointment Details Section */}
        <div className="appointment-details">
          <div className="upcoming-schedule">
            <h3>The Upcoming Schedule</h3>

            <div className="day-schedule">
              <h4>On Thursday</h4>
              <div className="appointment-cards">
                {thursdayAppointments.map((apt, index) => (
                  <div key={`thurs-apt-${index}`} className="appointment-card">
                    <div className="appointment-info">
                      <div className="apt-title">{apt.type}</div>
                      <div className="apt-time">{apt.time}</div>
                      {apt.doctor && <div className="apt-doctor">{apt.doctor}</div>}
                    </div>
                    <div className={`apt-icon ${apt.icon}`}>
                      {apt.icon === 'tooth' && 'ü¶∑'}
                      {apt.icon === 'physio' && 'üß†'}
                      {apt.icon === 'checkup' && '‚úì'}
                      {apt.icon === 'eye' && 'üëÅÔ∏è'}
                      {apt.icon === 'heart' && '‚ù§Ô∏è'}
                      {apt.icon === 'brain' && 'üß†'}
                    </div>
                  </div>
                ))}
              </div>
            </div>

            <div className="day-schedule">
              <h4>On Saturday</h4>
              <div className="appointment-cards">
                {saturdayAppointments.map((apt, index) => (
                  <div key={`sat-apt-${index}`} className="appointment-card">
                    <div className="appointment-info">
                      <div className="apt-title">{apt.type}</div>
                      <div className="apt-time">{apt.time}</div>
                      {apt.doctor && <div className="apt-doctor">{apt.doctor}</div>}
                    </div>
                    <div className={`apt-icon ${apt.icon}`}></div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CalendarView;
