import React, { useState } from 'react';
import { anatomyData } from '../../data/healthData';
import '../../styles/AnatomySection.css';

const AnatomySection = () => {
  const [rotation, setRotation] = useState(0);
  const [activeArea, setActiveArea] = useState(null);

  const handleRotationChange = (e) => {
    setRotation(e.target.value);
  };

  const handleMouseMove = (e) => {
    // Get mouse position relative to the image container
    const rect = e.currentTarget.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;

    // Check if mouse is over any of the health areas
    const heartArea = { x: rect.width * 0.5, y: rect.height * 0.32, radius: 30 };
    const teethArea = { x: rect.width * 0.5, y: rect.height * 0.15, radius: 25 };
    const boneArea = { x: rect.width * 0.5, y: rect.height * 0.76, radius: 35 };

    const distHeart = Math.sqrt(Math.pow(x - heartArea.x, 2) + Math.pow(y - heartArea.y, 2));
    const distTeeth = Math.sqrt(Math.pow(x - teethArea.x, 2) + Math.pow(y - teethArea.y, 2));
    const distBone = Math.sqrt(Math.pow(x - boneArea.x, 2) + Math.pow(y - boneArea.y, 2));

    if (distHeart < heartArea.radius) {
      setActiveArea('heart');
    } else if (distTeeth < teethArea.radius) {
      setActiveArea('teeth');
    } else if (distBone < boneArea.radius) {
      setActiveArea('bone');
    } else {
      setActiveArea(null);
    }
  };

  const handleMouseLeave = () => {
    setActiveArea(null);
  };

  return (
    <div className="anatomy-section">
      <div 
        className="anatomy-image-container"
        onMouseMove={handleMouseMove}
        onMouseLeave={handleMouseLeave}
      >
        <div className="search-icon-overlay">
          <span className="magnify-icon"></span>
        </div>

        <div className="anatomy-image">
          {/* Human body image with rotation */}
          <img 
            src="/human-anatomy-muscular.svg" 
            alt="Human anatomy" 
            className="body-image" 
            style={{ transform: `rotateY(${rotation}deg)` }}
          />

          {/* Health status indicators */}
          {anatomyData.map((item) => (
            <div 
              key={item.id}
              className={`health-indicator ${item.statusColor} ${activeArea === item.name.toLowerCase() ? 'active' : ''}`}
              style={{ 
                top: item.position.top || 'auto', 
                left: item.position.left || 'auto',
                right: item.position.right || 'auto',
                bottom: item.position.bottom || 'auto',
              }}
            >
              {activeArea === item.name.toLowerCase() && (
                <div className="status-bubble">
                  <span className={`icon-${item.name.toLowerCase()}`}></span>
                  <span className="status-text">{item.status}</span>
                </div>
              )}
            </div>
          ))}

          {/* Healthy Log Button */}
          <button className="healthy-log-button">
            <span className="log-icon"></span>
            <span className="log-text">Healthy Log</span>
          </button>
        </div>
      </div>

      <div className="rotation-controls">
        <div className="rotation-label">Rotate View (Y-axis)</div>
        <div className="slider-control">
          <input 
            type="range" 
            min="0" 
            max="360" 
            value={rotation}
            onChange={handleRotationChange}
            className="body-slider" 
          />
        </div>
      </div>
    </div>
  );
};

export default AnatomySection;
