import React, { useState } from 'react';
import { calendarData, specialAppointments } from '../../data/appointmentsData';
import SimpleAppointmentCard from './SimpleAppointmentCard';
import '../../styles/CalendarView.css';

const CalendarView = () => {
  const [selectedDay, setSelectedDay] = useState(28); // Default to the 28th (Thursday in the sample data)

  return (
    <div className="calendar-view">
      <div className="calendar-header">
        <div className="calendar-navigation">
          <button className="nav-arrow prev-arrow">
            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <path d="M15 18l-6-6 6-6" />
            </svg>
          </button>
          <h2 className="month-year">{`${calendarData.month} ${calendarData.year}`}</h2>
          <button className="nav-arrow next-arrow">
            <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round">
              <path d="M9 18l6-6-6-6" />
            </svg>
          </button>
        </div>
      </div>

      <div className="calendar-grid">
        <div className="week-days">
          {calendarData.days.map(day => (
            <div 
              key={day.date} 
              className={`week-day ${day.date === selectedDay ? 'selected' : ''}`}
              onClick={() => setSelectedDay(day.date)}
            >
              <p className="day-name">{day.day}</p>
              <p className="day-date">{day.date}</p>
            </div>
          ))}
        </div>

        <div className="time-slots">
          {/* Morning time slot (9-11) */}
          <div className="time-row morning">
            <div className="time-label-side">Morning</div>
            {calendarData.days.map(day => {
              const hasAppointment = day.appointments.some(apt => {
                const hour = parseInt(apt.time.split(':')[0]);
                return hour >= 9 && hour < 12;
              });
              return (
                <div 
                  key={`${day.date}-morning`} 
                  className={`time-cell ${hasAppointment ? 'has-appointment' : ''} ${day.date === selectedDay ? 'day-selected' : ''}`}
                >
                  {hasAppointment && (
                    <div className="time-slot-content">
                      <div className="time-slot-line"></div>
                      <span className="time-label">AM</span>
                    </div>
                  )}
                </div>
              );
            })}
          </div>

          {/* Afternoon time slot (12-15) */}
          <div className="time-row afternoon">
            <div className="time-label-side">Afternoon</div>
            {calendarData.days.map(day => {
              const hasAppointment = day.appointments.some(apt => {
                const hour = parseInt(apt.time.split(':')[0]);
                return hour >= 12 && hour < 15;
              });
              return (
                <div 
                  key={`${day.date}-afternoon`} 
                  className={`time-cell ${hasAppointment ? 'has-appointment' : ''} ${day.date === selectedDay ? 'day-selected' : ''}`}
                >
                  {hasAppointment && (
                    <div className="time-slot-content">
                      <div className="time-slot-line"></div>
                      <span className="time-label">PM</span>
                    </div>
                  )}
                </div>
              );
            })}
          </div>

          {/* Evening time slot (15-18) */}
          <div className="time-row evening">
            <div className="time-label-side">Evening</div>
            {calendarData.days.map(day => {
              const hasAppointment = day.appointments.some(apt => {
                const hour = parseInt(apt.time.split(':')[0]);
                return hour >= 15 && hour < 18;
              });
              return (
                <div 
                  key={`${day.date}-evening`} 
                  className={`time-cell ${hasAppointment ? 'has-appointment' : ''} ${day.date === selectedDay ? 'day-selected' : ''}`}
                >
                  {hasAppointment && (
                    <div className="time-slot-content">
                      <div className="time-slot-line"></div>
                      <span className="time-label">PM</span>
                    </div>
                  )}
                </div>
              );
            })}
          </div>
        </div>
      </div>

      <div className="special-appointments">
        {specialAppointments.map(appointment => (
          <div key={appointment.id} className={`appointment-card ${appointment.color}`}>
            <div className="appointment-icon">
              <span className={`icon-${appointment.icon}`}></span>
            </div>
            <div className="appointment-details">
              <h3 className="appointment-title">{appointment.title}</h3>
              <p className="appointment-time">{appointment.time}</p>
              <p className="appointment-doctor">{appointment.doctor}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CalendarView;
