import React, { useState } from 'react';
import { anatomyData } from '../../data/healthData';
import '../../styles/AnatomySection.css';

const AnatomySection = () => {
  const [rotation, setRotation] = useState(0);

  const handleRotationChange = (e) => {
    setRotation(e.target.value);
  };

  return (
    <div className="anatomy-section">
      <div className="anatomy-image-container">
        <div className="search-icon-overlay">
          <span className="magnify-icon"></span>
        </div>

        <div className="anatomy-image">
          {/* Human body image with rotation */}
          <img 
            src="/human-anatomy.svg" 
            alt="Human anatomy" 
            className="body-image" 
            style={{ transform: `rotate(${rotation}deg)` }}
          />

          {/* Health status indicators - rotated to stay in position */}
          {anatomyData.map((item) => (
            <div 
              key={item.id}
              className={`health-indicator ${item.statusColor}`}
              style={{ 
                top: item.position.top || 'auto', 
                left: item.position.left || 'auto',
                right: item.position.right || 'auto',
                bottom: item.position.bottom || 'auto',
                transform: `rotate(${-rotation}deg)` // Counter-rotate to stay upright
              }}
            >
              {item.name === 'Heart' && (
                <div className="heart-status-bubble">
                  <span className="heart-icon"></span>
                  <span className="status-text">Healthy Heart</span>
                </div>
              )}
            </div>
          ))}

          {/* Healthy Log Button */}
          <button className="healthy-log-button">
            <span className="log-icon"></span>
            <span className="log-text">Healthy Log</span>
          </button>
        </div>
      </div>

      <div className="rotation-controls">
        <div className="rotation-label">Rotate View (360Â°)</div>
        <div className="slider-control">
          <input 
            type="range" 
            min="0" 
            max="360" 
            value={rotation}
            onChange={handleRotationChange}
            className="body-slider" 
          />
        </div>
      </div>
    </div>
  );
};

export default AnatomySection;
